using FileFormatExplain;
using PM.Common;
using PM.FileEngine.Tests.PMemoryTransactionFile;
using PM.Tests.Common;
using System.Text;
using Xunit.Abstractions;

namespace PM.FileEngine.Tests
{
    public class PAllocatorTest : UnitTest
    {
        private const string OriginalFileSufix = "_OriginalFile.pm";
        private const string TransactionFileSufix = "_TransactionFile.pm";
        private readonly ITestOutputHelper _output;

        public PAllocatorTest(ITestOutputHelper output)
            : base(output)
        {
            _output = output;
        }

        [Fact]
        public void TransactionFileOperations()
        {
            DeleteFilesByMethodName(nameof(TransactionFileOperations));

            var streams = CreateStreamByMethodName(nameof(TransactionFileOperations));
            var pallocator = new PAllocator(streams.OriginalStream, streams.TransactionStream);

            byte[] transactionFile = pallocator.ReadTransactionFile();
            byte[] originalFile = pallocator.ReadOriginalFile();

            var transactionFileContentDecoded = TransactionFileDecoder.Decode_HexResponse(transactionFile);
            var originalFileContentDecoded = PMemoryDecoder.DecodeHex(originalFile);
            // 
            // HeaderCommitByte=01
            // Version=0001
            // BlockType=00
            //_output.WriteLine(transactionFileContentDecoded);
            Assert.StartsWith("0101000000000000000000000000000000", ByteArrayToHexStringConverter.ByteArrayToString(transactionFile));

            var content = Encoding.ASCII.GetBytes("Hello World");
            var region = pallocator.Alloc((uint)content.Length); // Create New block on this point

            transactionFile = pallocator.ReadTransactionFile();
            originalFile = pallocator.ReadOriginalFile();
            transactionFileContentDecoded = TransactionFileDecoder.Decode_HexResponse(transactionFile);
            originalFileContentDecoded = PMemoryDecoder.DecodeHex(originalFile);
            _output.WriteLine(transactionFileContentDecoded);
            _output.WriteLine(originalFileContentDecoded);
            // 
            // HeaderCommitByte=01
            // Version=0001
            // BlockType=04
            // =================UpdateFreeBlocksFromBlock=================
            // CommitByte=02
            // StartBlockOffset=0000000A
            // NewFreeBlocks=0000000000000001
            Assert.StartsWith("01010004020a000000010000", ByteArrayToHexStringConverter.ByteArrayToString(transactionFile));

            // 
            // CommitByte=01
            // StartBlocksOffset=00000005
            // ========Block 0========
            // RegionsQuantity=40
            // RegionsSize=00000010
            // FreeBlocks=0000000000000001
            // NextBlockAddress=00000000
            // Region 0: 00000000000000000000000000000000
            // ...
            // Region 63: 00000000000000000000000000000000

            Assert.StartsWith("0105000000401000000001000000000", ByteArrayToHexStringConverter.ByteArrayToString(originalFile));


            region.Write(content);
            transactionFile = pallocator.ReadTransactionFile();
            originalFile = pallocator.ReadOriginalFile();
            transactionFileContentDecoded = TransactionFileDecoder.Decode_HexResponse(transactionFile);
            originalFileContentDecoded = PMemoryDecoder.DecodeHex(originalFile);
            _output.WriteLine(transactionFileContentDecoded);
            _output.WriteLine(originalFileContentDecoded);
            // 
            // HeaderCommitByte=01
            // Version=0001
            // BlockType=03
            // =================UpdateContentBlockLayout=================
            // CommitByte=02
            // StartBlockOffset=00000016
            // ContentSize=0000000B
            // Content=48656c6c6f20576f726c64
            Assert.StartsWith("0101000302160000000b00000048656c6c6f20576f726c64", ByteArrayToHexStringConverter.ByteArrayToString(transactionFile));

            // 
            // CommitByte=01
            // StartBlocksOffset=00000005
            // ========Block 0========
            // RegionsQuantity=40
            // RegionsSize=00000010
            // FreeBlocks=0000000000000001
            // NextBlockAddress=00000000
            // Region 0: 48656c6c6f20576f726c640000000000
            // Region 1: 00000000000000000000000000000000
            // Region 63: 00000000000000000000000000000000
            Assert.StartsWith("0105000000401000000001000000000000000000000048656c6c6f20576f726c64", ByteArrayToHexStringConverter.ByteArrayToString(originalFile));

            var region2 = pallocator.Alloc((uint)content.Length);
            var content2 = Encoding.ASCII.GetBytes("Hello World2");
            region2.Write(content2);
            transactionFile = pallocator.ReadTransactionFile();
            originalFile = pallocator.ReadOriginalFile();
            transactionFileContentDecoded = TransactionFileDecoder.Decode_HexResponse(transactionFile);
            originalFileContentDecoded = PMemoryDecoder.DecodeHex(originalFile);
            _output.WriteLine(transactionFileContentDecoded);
            _output.WriteLine(originalFileContentDecoded);
            Assert.Equal(1, region2.RegionIndex);

            // old block is regions about 16 bytes.
            // Now create new block with more size
            var content3 = Encoding.ASCII.GetBytes(@"Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nullam ornare ante quis vestibulum tincidunt. Vestibulum ante ipsum primis in faucibus orci luctus et ultrices posuere cubilia curae; Proin in luctus neque. Maecenas eget ultricies purus. Nunc sed sapien ultricies, tristique diam id, viverra mi. Curabitur eu elit ut ex faucibus tempus. Nulla malesuada condimentum ornare. Quisque interdum eleifend libero dapibus sagittis.

Duis nec cursus elit. Donec maximus sagittis arcu, vel commodo turpis tempor eu. Etiam fermentum sapien sit amet blandit imperdiet. Quisque eget condimentum nulla. Maecenas scelerisque nunc odio, vel varius libero facilisis sed. Nulla blandit vehicula metus, sit amet laoreet justo suscipit nec. Ut interdum fringilla sagittis. Integer tristique augue turpis, elementum sollicitudin nunc auctor ac. Sed accumsan condimentum luctus. Quisque lobortis tortor libero, in dapibus ligula aliquet sed. Quisque cursus cursus libero sed porttitor. Donec bibendum auctor viverra. Proin at sapien non nunc fringilla auctor. Vivamus non nunc ut lacus dictum ultricies sit amet vitae justo. Morbi ligula turpis, molestie a egestas sit amet, iaculis quis tortor. Donec eget tempor felis.

Integer pretium metus sit amet erat consectetur, in euismod velit viverra. Duis euismod eros faucibus lectus elementum semper. Donec vel dolor id nunc mollis sodales quis eget leo. Aliquam elementum massa id lacus finibus lacinia. Pellentesque tincidunt auctor massa et auctor. Sed at tempus leo. Cras blandit orci quis libero blandit, ut tincidunt leo maximus. Phasellus ut elementum justo. Suspendisse blandit ante non nunc auctor vehicula.

Nam quis hendrerit diam. Etiam vulputate elementum metus in gravida. Proin magna odio, rhoncus vitae nisi vitae, pharetra interdum orci. Aenean condimentum diam ut felis lobortis, a iaculis velit pulvinar. Maecenas consectetur ac nibh in placerat. Nunc fringilla egestas elit, at faucibus turpis eleifend eget. Praesent blandit metus sed maximus placerat.");
            var region3 = pallocator.Alloc((uint)content3.Length);
            Assert.Equal(0, region3.RegionIndex);
            region3.Write(content3);
            Assert.Equal(content3, region3.Read((uint)content3.Length));
        }

        private static PmStreams CreateStreamByMethodName(string methodName)
        {
            return new PmStreams
            {
                OriginalStream = new PmCSharpDefinedTypes(CreatePmStream(methodName + OriginalFileSufix)),
                TransactionStream = new PmCSharpDefinedTypes(CreatePmStream(methodName + TransactionFileSufix))
            };
        }

        private static void DeleteFilesByMethodName(string methodName)
        {
            DeleteFile(methodName + OriginalFileSufix);
            DeleteFile(methodName + TransactionFileSufix);
        }
    }
}
