using FileFormatExplain;
using PM.Common;
using PM.Tests.Common;
using Xunit.Abstractions;

namespace PM.FileEngine.Tests.PMemoryTransactionFile
{
    public class TransactionFileTest : UnitTest
    {
        private const string OriginalFileSufix = "_OriginalFile.pm";
        private const string TransactionFileSufix = "_TransactionFile.pm";
        private readonly ITestOutputHelper _output;

        public TransactionFileTest(ITestOutputHelper output) 
            : base(output)
        {
            _output = output;
        }

        [Fact]
        public void TransactionFileOperations()
        {
            DeleteFilesByMethodName(nameof(TransactionFileOperations));

            var streams = CreateStreamByMethodName(nameof(TransactionFileOperations));
            var pallocator = new PAllocator(streams.OriginalStream, streams.TransactionStream);

            byte[] transactionFile = pallocator.ReadTransactionFile();
            byte[] originalFile = pallocator.ReadOriginalFile();

            var transactionFileContentDecoded = TransactionFileDecoder.Decode_HexResponse(transactionFile);
            // 
            // HeaderCommitByte=01
            // Version=0001
            // BlockType=00
            _output.WriteLine(transactionFileContentDecoded);
            Assert.Equal("0101000000000000000000000000000000", ByteArrayToHexStringConverter.ByteArrayToString(transactionFile));
        }

        private static PmStreams CreateStreamByMethodName(string methodName)
        {
            return new PmStreams
            {
                OriginalStream = new PmCSharpDefinedTypes(CreatePmStream(methodName + OriginalFileSufix)),
                TransactionStream = new PmCSharpDefinedTypes(CreatePmStream(methodName + TransactionFileSufix))
            };
        }

        private static void DeleteFilesByMethodName(string methodName)
        {
            DeleteFile(methodName + OriginalFileSufix + OriginalFileSufix);
            DeleteFile(methodName + OriginalFileSufix + TransactionFileSufix);
        }
    }
}
